import React, { useState, useEffect } from "react";
import Layout from "../core/Layout";
import { isAuthenticated } from "../auth";
import { Link } from "react-router-dom";
import { createVegetable } from "./apiFarmer";

const AddVegetable = () =>{
    const { user, token } = isAuthenticated();

    const [values, setValues] = useState({
        name: "",
        price: "",
        quantity: "",
        farmer: '{user._id}',
        photo: "",
        loading: false,
        error: "",
        createdVegetable: "",
        redirectToProfile: false,
        formData: ""
    });

    const {
        name,
        price,
        quantity,
        farmer,
        loading,
        error,
        createdVegetable,
        redirectToProfile,
        formData
    } = values;

    useEffect(() => {
        setValues({ ...values, formData: new FormData() });
    }, []);

    const handleChange = name => event => {
        const value =
            name === "photo" ? event.target.files[0] : event.target.value;
        formData.set(name, value);
        setValues({ ...values, [name]: value });
    };

    const clickSubmit = event => {
        //
    };

    const newPostForm = () => (
        <form className="mb-3" onSubmit={clickSubmit}>

            <div className="form-group">
                <label className="text-muted">ItemName</label>
                <input
                    onChange={handleChange("name")}
                    type="text"
                    className="form-control"
                    value={name}
                />
            </div>


            <div className="form-group">
                <label className="text-muted">Price</label>
                <input
                    onChange={handleChange("price")}
                    type="number"
                    className="form-control"
                    value={price}
                />
            </div>

            <div className="form-group">
                <label >Quantity</label>
                <input
                    onChange={handleChange("quantity")}
                    type="number"
                    className="form-control"
                    value={quantity}
                />
            </div>

            <h4>Post Photo</h4>
            <div className="form-group">
                <label className="btn btn-secondary">
                    <input
                        onChange={handleChange("photo")}
                        type="file"
                        name="photo"
                        accept="image/*"
                    />
                </label>
            </div>

            <button className="btn btn-outline-primary">Add Vegetable</button>
        </form>
    );


    return(
        <Layout
            title="Add a new vegetable"
            description={`Helloo ${user.name}, ready to add a new vegetable?`}
        >
            <div className="row">
                <div className="col-md-8 offset-md-2">
                   {newPostForm()}
                </div>
            </div>
        </Layout>
    );
};

export default AddVegetable;
