import React, { useState, useEffect } from "react";
import Layout from "./Layout";
import Search from "./Search";
import Card from "./Card";
import {getFarmers, getFilteredVegetables} from "./apiCore";
import { prices } from "./fixedPrices";
import {isAuthenticated} from "../auth";

const View = () => {

    const {user} = isAuthenticated();

    console.log(user._id);

    const [myFilters, setMyFilters] = useState({
        filters: { farmer_id: [], price: [] }
    });
    const [error, setError] = useState(false);
    const [limit, setLimit] = useState(6);
    const [skip, setSkip] = useState(0);
    const [size, setSize] = useState(0);
    const [filteredResults, setFilteredResults] = useState([]);

    const loadFilteredResults = newFilters => {
        console.log("New Filters ",newFilters);
        getFilteredVegetables(skip, limit, newFilters).then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setFilteredResults(data.data);
                setSize(data.size);
                setSkip(0);
            }
        });
    };

    const loadMore = () => {
        let toSkip = skip + limit;
        // console.log(newFilters);
        getFilteredVegetables(toSkip, limit, myFilters.filters).then(data => {
            if (data.error) {
                setError(data.error);
            } else {
                setFilteredResults([...filteredResults, ...data.data]);
                setSize(data.size);
                setSkip(toSkip);
            }
        });
    };

    const loadMoreButton = () => {
        return (
            size > 0 &&
            size >= limit && (
                <button onClick={loadMore} className="btn btn-warning mb-5">
                    Load more
                </button>
            )
        );
    };

    const handleFilters = (filters, filterBy) => {
        console.log("SHOP", filters, filterBy);
        const newFilters = { ...myFilters };
        newFilters.filters[filterBy] = filters;
        loadFilteredResults(myFilters.filters);
        setMyFilters(newFilters);
    };

    return (
        <Layout
            title="Your Vegetables"
            description=""
            className="container-fluid"
        >
        {handleFilters(filters, "farmer_id")}
        </Layout>
    );
};

export default View;
