import React, {useState} from 'react'
import Layout from '../core/Layout';

const Signup = () => {

	const [values, setValues] = useState({
		name:'',
		email:'',
		password:'',
		error:'',
		success: false

	});

	const {name, email, password} = values	

	const signup = (name, email, password) => {
		console.log(name, email, password);
	}

	const handleChange = name => event => {
		setValues({...values, error:false, [name]: event.target.value });
	};

	const clickSubmit = () => {
		event.preventDefault();
		signup(name, email, password);
	}

    const signUpForm = () => (
        <div className="auth-wrapper-up">
            <div className="auth-inner-up">
                <form>
                    <h3>Sign Up</h3>
                    <div className="form-group">
                        <label >Name</label>
                        <input onChange={handleChange('name')} type="text" className="form-control" placeholder="Name"/>
                    </div>

                    <div className="form-group">
                        <label >Email</label>
                        <input onChange={handleChange('email')} type="email" className="form-control" placeholder="Enter email" />
                    </div>

                    <div className="form-group">
                        <label >Password</label>
                        <input onChange={handleChange('password')} type="password" className="form-control" placeholder="Enter password"/>
                    </div>

                    <button onClick={clickSubmit} className="btn btn-primary btn-block">
                        Submit
                    </button>
                </form>
            </div>
        </div>
    );
	return (
        <Layout
            title=" "
            description=" "
            className="container col-md-4 offset-md-4" >
            {signUpForm()}
			{JSON.stringify(values)}
        </Layout>
	)
};

export default Signup;
