import React, { useState } from "react";
import { Link, Redirect } from "react-router-dom";
import moment from "moment";
import ShowImage from "./ShowImage"
import { addItem } from "./cartHelpers"
import "./nav.css"

const Card = ({ vegetable }) => {

    const [redirect, setRedirect] = useState(false);

    const addToCart = () => {
        addItem(vegetable, () => {
            setRedirect(true);
        });
    };


    const shouldRedirect = redirect => {
        if (redirect) {
            return <Redirect to="/cart" />;
        }
    };

    const showAddToCartButton = () => {
        return (
            <button
                onClick={addToCart}
                className="btn btn-outline-warning mb-2"
            >
                Add to cart
            </button>
        );
    };

  //  const remain = vegetable.quantity-vegetable.sold;
    return (
        <div className="col-4 mb-3">
            <div className="card">
                <div className="card-header">{<strong>{vegetable.name}</strong>}</div>
                <div className="card-body">
                    {shouldRedirect(redirect)}
                    <ShowImage item={vegetable} url="vegetables" />
                    <p className="black-10"> $ {vegetable.price} per kg</p>

                    <p className="black-8">
                    Added on {moment(vegetable.createdAt).fromNow()}
                    </p>
                    <br />
                    {showAddToCartButton()}
                </div>
            </div>
        </div>
    );
};

export default Card;
