import React, { useState, useEffect } from "react";
import { getFarmers, list} from "./apiCore";
import Card from "./Card";

const Search = () => {
    const [data, setData] = useState({
        farmers: [],
        farmer: "All",
        search: "",
        results: [],
        searched: false
    });

    const { farmers, farmer, search, results, searched } = data;

    const loadFarmers = () => {
        getFarmers().then(data => {
            if (data.error) {
                console.log(data.error);
            } else {
                setData({ ...data, farmers: data });
            }
        });
    };

    useEffect(() => {
        loadFarmers();
    }, []);

    const searchData = () => {
         console.log(search, farmer);
       /* if (search) {
            list({ search: search || undefined, farmer: farmer }).then(
